    async def send_quote(self, client: Client, message: Message):
        """Send random anime quote (works in both groups and PMs)"""
        try:
            quote_data = random.choice(self.quotes)
            formatted_quote = (
                f'"{quote_data["quote"]}"\n\n'
                f'- {quote_data["character"]} ({quote_data["anime"]})'
            )
            await message.reply_text(formatted_quote)
        except Exception as e:
            logger.error(f"Error sending quote: {e}")
            await message.reply_text(" Failed to send quote. Please try again.")

    async def subscribe_user(self, client: Client, message: Message):
        try:
            user_id = message.from_user.id
            logger.info(f"Attempting subscription for {user_id}")
            
            # Verify database operations work
            if not await self.bot.db.verify_quote_subscription_ops(user_id):
                await message.reply_text(" Database verification failed. Please try later.")
                return
                
            existing = await self.bot.db.quote_subscriptions.find_one({"user_id": user_id})
            if existing:
                logger.info(f"User {user_id} already subscribed")
                await message.reply_text(" You're already subscribed!")
                return
                
            result = await self.bot.db.quote_subscriptions.insert_one({
                "user_id": user_id,
                "username": message.from_user.username,
                "first_name": message.from_user.first_name,
                "date_subscribed": datetime.now(),
                "active": True
            })
            
            if result.inserted_id:
                logger.info(f"New subscription for {user_id}")
                await message.reply_text(" Successfully subscribed to daily quotes!")
            else:
                logger.error(f"Insert failed for {user_id}")
                await message.reply_text(" Failed to subscribe. Please try again.")
                
        except Exception as e:
            logger.error(f"Subscription error for {user_id}: {str(e)}", exc_info=True)
            await message.reply_text(" An unexpected error occurred. Please try again later.")
    async def unsubscribe_user(self, client: Client, message: Message):
        """Unsubscribe user from daily quotes (PM only)"""
        try:
            user_id = message.from_user.id
            result = await self.db.quote_subscriptions.delete_one({"user_id": user_id})
            
            if result.deleted_count > 0:
                await message.reply_text(" You've unsubscribed from daily quotes.")
            else:
                await message.reply_text(" You weren't subscribed.")
        except Exception as e:
            logger.error(f"Unsubscribe error: {e}")
            await message.reply_text(" Failed to unsubscribe. Please try again.")

    async def send_daily_quotes(self, client: Client):
        """Send quotes to all subscribers (automated task)"""
        try:
            subscribers = self.db.quote_subscriptions.find({"active": True})
            async for user in subscribers:
                try:
                    quote_data = random.choice(self.quotes)
                    formatted_quote = (
                        " **Daily Anime Quote** \n\n"
                        f'"{quote_data["quote"]}"\n\n'
                        f'- {quote_data["character"]} ({quote_data["anime"]})'
                    )
                    await client.send_message(
                        chat_id=int(user["user_id"]),
                        text=formatted_quote
                    )
                except pyrogram_errors.UserIsBlocked:
                    logger.warning(f"User {user['user_id']} blocked the bot. Unsubscribing...")
                    await self.db.quote_subscriptions.update_one(
                        {"user_id": user["user_id"]},
                        {"$set": {"active": False}}
                    )
                except pyrogram_errors.ChatWriteForbidden:
                    logger.warning(f"Bot can't write to {user['user_id']}. Unsubscribing...")
                    await self.db.quote_subscriptions.update_one(
                        {"user_id": user["user_id"]},
                        {"$set": {"active": False}}
                    )
                except Exception as e:
                    logger.error(f"Error sending to {user['user_id']}: {e}")
        except Exception as e:
            logger.error(f"Daily quotes task failed: {e}")

    async def start_daily_task(self, client: Client):
        """Start the daily quotes scheduler"""
        asyncio.create_task(self._daily_quotes_task(client))

    async def _daily_quotes_task(self, client: Client):
        """Background task that runs daily at 9 AM"""
        while True:
            now = datetime.now()
            target_time = now.replace(hour=9, minute=0, second=0, microsecond=0)
            
            if now > target_time:
                target_time = target_time.replace(day=now.day + 1)
            
            wait_seconds = (target_time - now).total_seconds()
            await asyncio.sleep(wait_seconds)
            
            try:
                await self.send_daily_quotes(client)
            except Exception as e:
                logger.error(f"Daily quotes error: {e}")
            
            await asyncio.sleep(3600)  # Prevent multiple runs
